import React, { useState } from 'react';
import PropTypes from 'prop-types';
import { DataGrid } from '@mui/x-data-grid';
import profilePicture from './assets/profile.png';
import AddContactModal from '../components/AddContactModal';
import AddListModal from '../AddListModal';

import { InputBase, Button } from '@mui/material';

import {
  AppBar,
  Avatar,
  Box,
  CssBaseline,
  Divider,
  Drawer,
  IconButton,
  List,
  ListItemButton,
  ListItemIcon,
  ListItemText,
  Toolbar,
  Typography,
} from '@mui/material';
import {
  Menu as MenuIcon,
  Devices as DevicesIcon,
  Draw as DrawIcon,
  CalendarMonth as CalendarMonthIcon,
  Chair as ChairIcon,
  Contacts as ContactsIcon,
  Logout as LogoutIcon,
  AddBox as AddBoxIcon,
} from '@mui/icons-material';
import Logo from './assets/LOGO.png';
import EditIcon from '@mui/icons-material/Edit'
import EmailIcon from '@mui/icons-material/Email';
import SearchIcon from '@mui/icons-material/Search';
import { Grid } from '@mui/material';
const columns = [
  { field: 'id', headerName: 'ID', width: 100 },
  { field: 'emailAddress', headerName: 'Email Address', width: 250 },
  { field: 'phoneNumber', headerName: 'Phone Number', width: 200 },
  { field: 'company', headerName: 'Company', width: 200 },
  { field: 'companyWebsite', headerName: 'Company Website', width: 250 },
  {
    field: 'email',
    headerName: 'Email',
    width: 200,
    renderCell: (params) => (
      <Button
        style={{ background: 'none', border: 'none', padding: 0 }}
        onClick={() => handleEmailButtonClick(params.row.emailAddress)}
      >
        <EmailIcon style={{ color: '#000425' }} />
      </Button>
    ),
  },
  {
    field: 'details',
    headerName: 'Details',
    width: 150,
    renderCell: (params) => (
      <Button
        variant="contained"
        style={{ backgroundColor: '#000425', color: '#FFFFFF',borderRadius: '20px'  }}
        onClick={() => handleDetailsButtonClick(params.row.details)}
      >
        Details
      </Button>
    ),
  },{
    field: 'edit',
    headerName: 'Edit',
    width: 200,
    renderCell: (params) => (
      <Button
        style={{ background: 'none', border: 'none', padding: 0 }}
        onClick={() => handleEmailButtonClick(params.row.emailAddress)}
      >
        <EditIcon style={{ color: '#000425' }} />
      </Button>
    ),
  },

];

const rows = [
  { id: 1, emailAddress: 'john@example.com', phoneNumber: '1234567890', company: 'ABC Inc.', companyWebsite: 'www.abc.com', details: 'Details about John' },
  { id: 2, emailAddress: 'jane@example.com', phoneNumber: '0987654321', company: 'XYZ Corp.', companyWebsite: 'www.xyz.com', details: 'Details about Jane' },
  { id: 3, emailAddress: 'jane@example.com', phoneNumber: '0987654321', company: 'XYZ Corp.', companyWebsite: 'www.xyz.com', details: 'Details about Jane' },
  { id: 4, emailAddress: 'jane@example.com', phoneNumber: '0987654321', company: 'XYZ Corp.', companyWebsite: 'www.xyz.com', details: 'Details about Jane' },
  { id: 5, emailAddress: 'jane@example.com', phoneNumber: '0987654321', company: 'XYZ Corp.', companyWebsite: 'www.xyz.com', details: 'Details about Jane' },
  { id: 6, emailAddress: 'jane@example.com', phoneNumber: '0987654321', company: 'XYZ Corp.', companyWebsite: 'www.xyz.com', details: 'Details about Jane' },
  { id: 8, emailAddress: 'jane@example.com', phoneNumber: '0987654321', company: 'XYZ Corp.', companyWebsite: 'www.xyz.com', details: 'Details about Jane' },
  { id: 9, emailAddress: 'jane@example.com', phoneNumber: '0987654321', company: 'XYZ Corp.', companyWebsite: 'www.xyz.com', details: 'Details about Jane' },
  { id: 10, emailAddress: 'jane@example.com', phoneNumber: '0987654321', company: 'XYZ Corp.', companyWebsite: 'www.xyz.com', details: 'Details about Jane' },
  { id: 11, emailAddress: 'jane@example.com', phoneNumber: '0987654321', company: 'XYZ Corp.', companyWebsite: 'www.xyz.com', details: 'Details about Jane' },
  { id: 12, emailAddress: 'jane@example.com', phoneNumber: '0987654321', company: 'XYZ Corp.', companyWebsite: 'www.xyz.com', details: 'Details about Jane' },
  { id: 13, emailAddress: 'jane@example.com', phoneNumber: '0987654321', company: 'XYZ Corp.', companyWebsite: 'www.xyz.com', details: 'Details about Jane' },
  { id: 14, emailAddress: 'jane@example.com', phoneNumber: '0987654321', company: 'XYZ Corp.', companyWebsite: 'www.xyz.com', details: 'Details about Jane' },
  { id: 15, emailAddress: 'jane@example.com', phoneNumber: '0987654321', company: 'XYZ Corp.', companyWebsite: 'www.xyz.com', details: 'Details about Jane' },
  { id: 16, emailAddress: 'jane@example.com', phoneNumber: '0987654321', company: 'XYZ Corp.', companyWebsite: 'www.xyz.com', details: 'Details about Jane' },
  { id: 17, emailAddress: 'jane@example.com', phoneNumber: '0987654321', company: 'XYZ Corp.', companyWebsite: 'www.xyz.com', details: 'Details about Jane' },

  // Add more rows as needed
];

const handleEmailButtonClick = (emailAddress) => {
  // Handle email button click
};

const handleDetailsButtonClick = (details) => {
  // Handle details button click
};

const drawerWidth = 240;

function ResponsiveDrawer(props) {
  const { window } = props;
  const [mobileOpen, setMobileOpen] = React.useState(false);
  const [isClosing, setIsClosing] = React.useState(false);

  const [addContactOpen, setAddContactOpen] = useState(false);
  const [addListOpen, setAddListOpen] = useState(false);

  const handleAddContactOpen = () => {
    setAddContactOpen(true);
  };

  const handleAddContactClose = () => {
    setAddContactOpen(false);
  };

  const handleAddListOpen = () => {
    setAddListOpen(true);
  };

  const handleAddListClose = () => {
    setAddListOpen(false);
  };

  const handleDrawerClose = () => {
    setIsClosing(true);
    setMobileOpen(false);
  };

  const handleDrawerTransitionEnd = () => {
    setIsClosing(false);
  };

  const handleDrawerToggle = () => {
    if (!isClosing) {
      setMobileOpen(!mobileOpen);
    }
  };

  const drawer = (
    <div style={{ display: 'flex', flexDirection: 'column', height: '100%' }}>
      <Toolbar>
        <img src={Logo} alt="Logo" style={{ width: '100%', height: 'auto' }} />
      </Toolbar>
      <Divider />
      <List sx={{ flexGrow: 1 }}>
        <ListItemButton
         onClick={handleAddListOpen}
          sx={{
          '&:hover': {
            backgroundColor: '#535561',
          },
          '.MuiListItemText-primary, .MuiListItemText-secondary, .MuiListItemIcon-root': {
            color: '#AFB0B5',
          }
        }}>
          <ListItemText primary="List" />
          <IconButton edge="end" color="inherit">
            <AddBoxIcon style={{ color: '#AFB0B5' }} /> 
          </IconButton>
        </ListItemButton>
        <Divider />
        <ListItemButton sx={{
          '&:hover': {
            backgroundColor: '#535561'
          },
          '.MuiListItemText-primary, .MuiListItemText-secondary, .MuiListItemIcon-root': {
            color: '#AFB0B5',
          }
        }}>
          <ListItemIcon>
            <ContactsIcon />
          </ListItemIcon>
          <ListItemText primary="All Contacts" secondary="10,000" />
        </ListItemButton>
        <ListItemButton sx={{
          '&:hover': {
            backgroundColor: '#535561'
          },
          '.MuiListItemText-primary, .MuiListItemText-secondary, .MuiListItemIcon-root': {
            color: '#AFB0B5',
          }
        }}>
          <ListItemIcon>
            <CalendarMonthIcon />
          </ListItemIcon>
          <ListItemText primary="Event Organisation" secondary="6,000" />
        </ListItemButton>
        <ListItemButton sx={{
          '&:hover': {
            backgroundColor: '#535561'
          },
          '.MuiListItemText-primary, .MuiListItemText-secondary, .MuiListItemIcon-root': {
            color: '#AFB0B5',
          }
        }}>
          <ListItemIcon>
            <DrawIcon />
          </ListItemIcon>
          <ListItemText primary="UI/UX Design" secondary="10,000" />
        </ListItemButton>
        <ListItemButton sx={{
          '&:hover': {
            backgroundColor: '#535561'
          },
          '.MuiListItemText-primary, .MuiListItemText-secondary, .MuiListItemIcon-root': {
            color: '#AFB0B5',
          }
        }}>
          <ListItemIcon>
            <DevicesIcon />
          </ListItemIcon>
          <ListItemText primary="IT" secondary="6,000" />
        </ListItemButton>
        <ListItemButton sx={{
          '&:hover': {
            backgroundColor: '#535561'
          },
          '.MuiListItemText-primary, .MuiListItemText-secondary, .MuiListItemIcon-root': {
            color: '#AFB0B5',
          }
        }}>
          <ListItemIcon>
            <ChairIcon />
          </ListItemIcon>
          <ListItemText primary="Interior Design" secondary="3,000" />
        </ListItemButton>

      </List>
      {/* Logout */}
      <List>
        <Divider />
        <ListItemButton sx={{
          '&:hover': {
            backgroundColor: '#535561'
          },
          '.MuiListItemText-primary, .MuiListItemText-secondary, .MuiListItemIcon-root': {
            color: '#AFB0B5',
          }
        }}>
          <ListItemIcon>
            <LogoutIcon />
          </ListItemIcon>
          <ListItemText primary="Logout" />
        </ListItemButton>
      </List>
    </div>
  );

  const container = window !== undefined ? () => window().document.body : undefined;

  return (
    <Box sx={{ display: 'flex' }}>
      <CssBaseline />
      <AppBar
  position="fixed"
  sx={{
    width: { sm: `calc(100% - ${drawerWidth}px)` },
    ml: { sm: `${drawerWidth}px` },
    backgroundColor: '#000425', // Set the background color here
  }}
>
  <Toolbar>
    <IconButton
      color="inherit"
      aria-label="open drawer"
      edge="start"
      onClick={handleDrawerToggle}
      sx={{ mr: 2, display: { sm: 'none' } }}
    >
      <MenuIcon />
    </IconButton>

    <Avatar sx={{ ml: 'auto' }} alt="Profile Picture" src={profilePicture} />
    </Toolbar>
</AppBar>

<Box
  component="nav"
  sx={{ width: { sm: drawerWidth }, flexShrink: { sm: 0 } }}
  aria-label="mailbox folders"
>
  <Drawer
    container={container}
    variant="temporary"
    open={mobileOpen}
    onTransitionEnd={handleDrawerTransitionEnd}
    onClose={handleDrawerClose}
    ModalProps={{
      keepMounted: true,
    }}
    sx={{
      display: { xs: 'block', sm: 'none' },
      '& .MuiDrawer-paper': { boxSizing: 'border-box', width: drawerWidth, backgroundColor: '#000425' }, // Background color for mobile view
    }}
  >
    {drawer}
  </Drawer>
  <Drawer
    sx={{
      width: drawerWidth,
      flexShrink: 0,
      '& .MuiDrawer-paper': {
        width: drawerWidth,
        boxSizing: 'border-box',
        backgroundColor: '#000425', // Sidebar background color
        color: '#AFB0B5', // Text and icon color
      },
      display: { xs: 'none', sm: 'block' },
    }}
    variant="permanent"
    open
  >
    {drawer}
  </Drawer>
</Box>

      <Box
        component="main"
        sx={{ flexGrow: 1, p: 3, width: { sm: `calc(100% - ${drawerWidth}px)` } }}
      >
        <Toolbar />
        <Typography variant="h4"><b>Event Organisation</b></Typography>
  <div style={{ marginBottom: '20px' }}></div> {/* Add space between heading and button */}
  <Grid container alignItems="center" justifyContent="space-between">
      {/* Search Bar */}
      <Grid item xs={2} sx={{ pl: 1 }}>
      <div style={{ position: 'relative', backgroundColor: '#D9D9D9', borderRadius: '20px' }}>
        <InputBase
          placeholder="Search..."
          fullWidth
          startAdornment={<SearchIcon sx={{ pl: 1}} />}
          style={{ paddingRight: '30px' }}
        />
      </div>
    </Grid>

      {/* Add Button */}
      <Grid item xs={4}>
        <div style={{ display: 'flex', alignItems: 'center', justifyContent: 'flex-end' }}>
          <Button
            variant="contained"
            style={{ backgroundColor: '#000425', color: 'white', borderRadius: '20px' }}
            endIcon={<AddBoxIcon />}
            onClick={handleAddContactOpen}
          >
            Add new
          </Button>
        </div>
      </Grid>
    </Grid>


  <div style={{ marginBottom: '20px' }}></div> {/* Add space between heading and table */}
  <div style={{ height: '100%', width: '100%', overflowX: 'auto' }}>
    <div style={{ minWidth: 800 }}> 
      <DataGrid
        rows={rows}
        columns={columns}
        pageSize={7}
        checkboxSelection
        headerClassName="boldHeader"
      />
    </div>
  </div>
  <AddContactModal open={addContactOpen} onClose={handleAddContactClose} />
  <AddListModal open={addListOpen} onClose={handleAddListClose} />
      </Box>
    </Box>
  );
}

ResponsiveDrawer.propTypes = {
  window: PropTypes.func,
};

export default ResponsiveDrawer;
